mapper.select:
SELECT count(1) FROM tb_order A JOIN tb_user U ON A.UserID = U.ID LEFT JOIN tb_school S ON A.SchoolID = S.ID JOIN tb_student T ON A.StudentID = T.ID JOIN tb_pay P ON A.PayID = P.ID LEFT JOIN tb_basicdict B ON S.Country = B.`Code` WHERE 1=1
AND ( A.OrderNo LIKE CONCAT('%',#{key},'%') OR U.Telphone LIKE CONCAT('%',#{key},'%') OR U.Email LIKE CONCAT('%',#{key},'%') OR U.Name LIKE CONCAT('%',#{key},'%') )
AND U.source = #{source}
AND A.FinishFlg = #{state}
AND DATE_FORMAT(A.CreatedTime,'%Y-%m-%d') >= #{timeBegin,jdbcType=VARCHAR}
AND DATE_FORMAT(A.CreatedTime,'%Y-%m-%d') <= #{timeEnd,jdbcType=VARCHAR}

SELECT A.OrderNo, A.PayFlg, A.OrderSeq, A.PayResult, A.FinishFlg, U.Telphone, U.Email, A.CreatedTime, T.ChName, T.PyName, T.CardNo, T.Remarks, T.VoucherUrl, P.PriceCN, P.PriceEN, S.AccountName, S.EnName, S.AccountNo, B.Text AS UnitText, A.FinishFlg, S.BankAddress, S.BIC, S.Country, U.Name FROM tb_order A JOIN tb_user U ON A.UserID = U.ID LEFT JOIN tb_school S ON A.SchoolID = S.ID JOIN tb_student T ON A.StudentID = T.ID JOIN tb_pay P ON A.PayID = P.ID LEFT JOIN tb_basicdict B ON S.Country = B.`Code` WHERE 1=1
AND ( A.OrderNo LIKE CONCAT('%',#{key},'%') OR U.Telphone LIKE CONCAT('%',#{key},'%') OR U.Email LIKE CONCAT('%',#{key},'%') OR U.Name LIKE CONCAT('%',#{key},'%') )
AND U.source = #{source}
AND A.FinishFlg = #{state}
AND DATE_FORMAT(A.CreatedTime,'%Y-%m-%d') >= #{timeBegin,jdbcType=VARCHAR}
AND DATE_FORMAT(A.CreatedTime,'%Y-%m-%d') <= #{timeEnd,jdbcType=VARCHAR}
ORDER BY A.CreatedTime DESC LIMIT #{page},#{count}
